/*------------------------------------*\
    #Searchbox
\*------------------------------------*/
div#simpleSearch {
    background: rgba(255, 255, 255, .2);
    transition: all .3s ease-out;
    width: 8%;
    position: fixed;
    left: 20px;
    top: 14px;
    opacity: 0;
}

div#simpleSearch:hover {
    opacity: 1;
}

pre,
code,
tt,
kbd,
samp,
.mw-code,
textarea,
input,
.mw-editfont-monospace {
    font-family: monospace;
}





/*------------------------------------*\
    #Heimu (for https://moegirl.org)
\*------------------------------------*/
.heimu,
.heimu a {
    background-color: transparent !important;
    filter: blur(5px);
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    transition: all 200ms 300ms;
}

.heimu a {
    color: #5DAC81 !important;
}

.heimu:hover {
    filter: blur(.2px);
    -webkit-filter: blur(.2px);
    -moz-filter: blur(.2px);
    -o-filter: blur(.2px);
    color: black !important;
}

.heimu a:hover {
    filter: blur(.2px);
    -webkit-filter: blur(.2px);
    -moz-filter: blur(.2px);
    -o-filter: blur(.2px);
    color: #5DAC81 !important;
}





/*------------------------------------*\
    #Top
\*------------------------------------*/
#contentSub, #contentSub2 {
    padding-top: 1em;
}
.topButton1,
.topButton2 {
    position: absolute;
    width: 30px;
    top: 20px;
    transition: all 300ms ease;
}

.topButton1:hover,
.topButton2:hover {
    opacity: .8;
}

.topButton1 {
    left: 20px;
}

.topButton2 {
    right: 20px;
}

#mw-head-base {
    margin-top: -5em;
    margin-left: 0 !important;
    height: 5em;
    box-shadow: 0 140px 0 300px var(--m-color);
    background: var(--m-color);
    width: 100%;
}

div#p-personal {
    width: 100%;
    position: fixed;
    top: 0;
    right: 0;
    padding: .7em !important;
    background: var(--m-color);
    box-shadow: 0 2px 6px rgba(0, 0, 0,  .2);
    justify-content: space-between;
    z-index: 1 !important;
}

#p-personal ul {
    float: right;
    color: var(--m-txtcolor);
    background: transparent;
    transition: all .3s ease;
    opacity: 0;
}

#p-personal ul:hover {
    opacity: 1;
}

#p-personal a, #p-personal a.new, #p-personal a.new:visited {
    color: var(--m-txtcolor);
}

#pt-notifications-alert .mw-echo-notifications-badge,
#pt-notifications-notice .mw-echo-notifications-badge {
    color: transparent !important;
}

.mw-echo-ui-notificationBadgeButtonPopupWidget > .oo-ui-popupWidget,
#p-personal a.oo-ui-buttonElement-button {
    color: rgba(0, 0, 0, .9);
}

.topTitle {
    color: var(--m-txtcolor);
    float: left;
    position: absolute;
    left: 18%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 20em;
}





/*------------------------------------*\
    #Main
\*------------------------------------*/
strong, b {
    font-weight: 500;
}
::selection {
    background: #DAEAFF;
}

html {
    font-size: 18px;
}

ul {
    list-style: url();
}

.comment-replybox .comment-avatar {
    margin: 0 30px;
}

.comment-avatar img {
    border-radius: 50%;
    transition: all 300ms ease;
}

.comment-avatar img:hover {
    border-radius: 2px;
    transition: all 300ms ease;
}

.comment-container {
    padding: 1em;
}

@-webkit-keyframes main {
    from {
        margin-top: 200px;
        opacity: 0;
    }
    to {
        margin-top: 165px;
        opacity: 1;
    }
}

.ns-0 div#content,
.ns-0 div#p-cactions li a:hover,
.ns-0 div#p-cactions li.selected a,
.ns-0 div#content div.thumb,
div#content,
div#p-cactions li a:hover,
div#p-cactions li.selected a,
div#content div.thumb {
    background: #fff;
}

#content {
    animation: main 500ms;
    background: #fff;
    margin-left: auto;
    margin-right: auto;
    margin-top: 165px;
    width: 60%;
    box-shadow: 0 4px 5px 0 rgba(0,0,0,.14), 0 1px 10px 0 rgba(0,0,0,.12), 0 2px 4px -1px rgba(0,0,0,.2);
}

@media screen and (max-width: 767px) {
    #content {
        width: 100%;
        border-radius: 0;
    }
}

.mw-content-ltr ul, .mw-content-rtl .mw-content-ltr ul, .mw-content-ltr table ol, .mw-content-rt.mw-content-ltr table ol {
    margin-left: auto;
}

.mw-body-content h1,.mw-body-content h2,.mw-body-content h3, .mw-body-content h4, .mw-body-content h5.mw-body-content h6{

    color: #616161;
    font-weight: 500;
}
::-webkit-input-placeholder,
::input-placeholder{
    color: rgba(0,0,0,.8);
}

#firstHeading {
    position: absolute;
    top: 100px;
    left: 0;
    color: var(--m-txtcolor) !important;
    width: 100%;
    text-align: center;
    transition: all 300ms ease;
    padding-left: 0;
    display: inline !important;
    font-weight: 300;
}

#firstHeading:hover {
    opacity: .8;
}

span.subpages {
    margin-left: 10px;
}

.mw-body {
    padding: 0;
}

.mw-body-content {
    color: rgba(0,0,0,.7);
    margin: 1em calc(1em + 1pc);
}

#mw-page-base,
body.skin-vector {
    background: #e9ebec;
}

a,
.mw-body-content a.extiw,
.mw-body-content a.extiw:active,
.mw-body-content a.external {
    color: var(--m-acolor);
    text-decoration: none !important;
    transition: color .3s ease;
}

a:visited,
.mw-body-content a.external:visited,
.mw-body-content a.extiw:visited {
    color: var(--m-avcolor);
}

a:active,
.mw-body-content a.external:active,
.mw-body-content a.extiw:active {
    color: var(--m-aacolor);
}
a:hover,
.mw-body-content a.external:hover,
.mw-body-content a.extiw:hover {
    color: var(--m-ahcolor);
}

h1 {
    border-bottom: none;
}

