}





/*------------------------------------*\
    #Contents
\*------------------------------------*/
#toc,
.toc{
    margin-right: -15%;
    position: fixed;
    right: 0;
    top: 0;
    height: 100vh !important;
    padding: 80px 20px 20px 20px;
    width: 15% !important;
    background: rgba(8, 8, 8, .8) !important;
    box-shadow: 10px 0 30px rgba(0, 0, 0, .2);
    z-index: 2;
    opacity: 0;
    font-size: 1em;
    overflow-y: auto;
    transition: all cubic-bezier(.08, .82, .17, 1) 300ms;
}

.mw-content-ltr .toc ul, .mw-content-ltr #toc ul, .mw-content-rtl .mw-content-ltr .toc ul, .mw-content-rt.mw-content-ltr #toc ul {
    max-height: calc(100vh - 100px);
    overflow-y: auto;
}

.toctext,
#toccontent li a span,
.tocnumber {
    color: rgba(225, 225, 225, .8);   
}

#toc:hover,
.toc:hover{
    margin-right: 0;
    opacity: 1;
}

.mainpage-1stcontent {
    padding: 0;
}






/*------------------------------------*\
    #Panel
\*------------------------------------*/
div#mw-panel {
    margin-left: -15%;
    position: fixed;
    left: 0;
    top: 0;
    height: 100vh !important;
    padding: 80px 20px 20px 20px;
    width: 15% !important;
    background: rgba(8, 8, 8, .8) !important;
    box-shadow: 10px 0 30px rgba(0, 0, 0, .2);
    z-index: 0;
    opacity: 0;
    font-size: .9rem;
    overflow-y: auto;
    transition: all cubic-bezier(.08, .82, .17, 1) 300ms;
}

div#mw-panel div.portal div.body ul li a ,
div#mw-panel div.portal div.body ul li a:visited,
div#mw-panel div.portal h3{
    color: rgba(225, 225, 225, .8);
    text-shadow: none;
}

div#mw-panel div.portal div.body {
    background: none;
}

div#mw-panel div.portal div.body ul li,
div#mw-panel div.portal h3{
    font-size: inherit;
    line-height: 1rem;
}

div#mw-panel:hover {
    margin-left: 0;
    opacity: 1;
}





/*------------------------------------*\
    #Thumbimg
\*------------------------------------*/
div.thumbinner {
    padding: 8px;
    overflow: visible;
    margin-top: 30px;
}

.thumbimage {
    margin-top: -30px;
