}

/*【自动跳转吧提示】*/
.polysemant-redirect-section {
	border-radius: 10px;
	background-color: var(--m-ecolor);
	color: #fff;
	border:none !important;
}

.polysemant-redirect-section *{
	color: inherit !important;
}

.polysemant-redirect-section>.close-button{
	font-size: 0 !important;
	position: absolute;
	top: 0;
	right: 10px;
	height: 100%;
	width: 24px !important;
	margin: 0 !important;
	background: none !important;
}

.polysemant-redirect-section>.close-button:before {
	content: "\e5c9";
	font-family: "Material Icons";
	font-size: 24px;
	display: block;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	-moz-transform: translate(-50%, -40%);
}

/*【贴吧页内容域】*/
.forum_content {
