	-webkit-user-select: none;
	height: 45px !important;
	background: var(--m-block-d002) !important;

	border-bottom: 1px solid var(--m-line-d010) !important;
	color: var(--m-light-40);
}

.p_thread.thread_theme_7 {
	border-top: 1px solid var(--m-line-d010) !important;
}

.l_thread_info {
	height: 100% !important;
	width: 100% !important;
	margin: 0 !important;
	display: flex;
	position: absolute;
}

.l_thread_info > * {
	float: left !important;
}

.l_posts_num {
	height: 100% !important;
	float: left;
	line-height: 1 !important;
	white-space: nowrap;
	font-size: 0;
	flex: 1
}

.l_posts_num li {
	height: 100% !important;
	display: inline-block !important;
	vertical-align: top;
	margin: 0 !important;
	box-sizing: border-box;
}

.l_posts_num > li:empty,
.thread_theme_bright_absolute .l_posts_num > li:empty {
	display: none !important;
}

#tofrs_up {
	display: none !important;
}

.pb_list_pager {
	padding-top: 10px !important;
	padding-left: 20px !important;
}

.p_thread input {
	position: relative;
	width: 80px !important;
	height: 30px !important;
	background: var(--m-block-d004);
	outline: none !important;
	border: none !important;
	margin: 0 !important;
	padding: 0 4px !important;
	margin-top: 7px !important;
	border-bottom: 4px solid hsla(0,0%,var(--m-block-darken-lightness),0.04) !important;
	box-sizing: border-box;
	color: transparent !important;
	font-size: 18px !important;
	line-height: 26px !important;
	padding-top: 4px !important;
	text-align: center;
	transition-property: transform, box-shadow, background;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
	-moz-appearance: textfield;
}

.p_thread input::-webkit-inner-spin-button {
	-webkit-appearance: none;
}

.p_thread input::-webkit-outer-spin-button {
	-webkit-appearance: none;
}

.p_thread input~button {
	display: block;
	position: absolute;
	width: 80px;
	padding: 0 !important;
	margin: 0 !important;
	top: 7px;
	height: 30px;
	border: 0 !important;
	outline: none;
	background: transparent !important;
	font-size: 0;
	pointer-events: none;
	animation-name: eventoff_duration;
	animation-duration: .5s;
	animation-timing-function: linear;
	animation-fill-mode: forwards;
	transition-property: transform;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.p_thread input~button:after {
	content: "";
	position: absolute;
	font-size: 16px;
	left: 8px;
	top: 0;
	line-height: 28px;
	color: var(--m-light-50);
}

.p_thread input[id^="jumpPage"]~button:after {
	content: "跳转到页";
}

.p_thread input[name="elevatorFloor"]~button:after {
	content: "跳转到楼";
}

.p_thread input[id^="jumpPage"]:focus~button:after {
	content: "页";
}

.p_thread input[name="elevatorFloor"]:focus~button:after {
	content: "楼";
}

.p_thread input:focus {
	background: var(--m-ecolor) !important;
	color: #fff !important;
	border-bottom: 4px solid rgba(0,0,0,0) !important;
	box-shadow: 0 2px 8px 0 rgba(0,0,0,0.2), 0 2px 12px 0 rgba(0,0,0,0.1);
	transform: scale(1.5);
	z-index: 9999;
}

.p_thread input:focus~button {
	animation: none !important;
	pointer-events: auto !important;
	transform: scale(1.5);
	z-index: 9999;
}

.p_thread input:focus~button:before,
.p_thread input:focus~button:after {
	position: absolute;
	font-size: 14px;
	top: 0;
	line-height: 30px;
	color: #fff;
	background: var(--m-ecolor);
	box-shadow: 0 2px 8px 0 rgba(0,0,0,0.2), 0 2px 12px 0 rgba(0,0,0,0.1);
	animation-duration: .5s;
	animation-timing-function: ease;
	animation-fill-mode: forwards;
}

.p_thread input:focus~button:before {
	content: "跳到";
	padding: 0 4px;
	left: -22px;
	border-radius: 20px 0 0 20px;
	animation-name: button_effect_left;
}

.p_thread input:focus~button:after {
	left: auto;
	right: -13px;
	padding: 0 6px;
	border-radius: 0 20px 20px 0;
	animation-name: button_effect_right;
}

.l_reply_num~.l_reply_num {
